import serial

settings = {'port': 'com3',
            'baud': 4800}


def get_port():
    prt = serial.Serial()
    prt.port = settings['port']
    prt.baudrate = int(settings['baud'])
    prt.bytesize = serial.SEVENBITS
    prt.parity = serial.PARITY_EVEN
    prt.stopbits = 2
    prt.xonxoff = True
    return prt

